INSERT INTO `director_info` VALUES ('西游记之大圣归来', '田晓鹏', 'Xiao Peng Tian', '', '田晓鹏，男，1975年出生，北京人，汉族，十月数码集团董事，国内资深三维动画影片导演，十八年三维动画创作经验。精通三维动画的创作工艺和流程，以本人为核心凝聚着国内最精坚的三维动画原创团队。\n\n2015年7月，导演的动画电影《西游记之大圣归来》上映。', '', '', '', '0000-0-0', '北京', '', '中国', '', '', 0, 0, '', '', '导演\n编剧', '', '', '', 0, 0);
