INSERT INTO `director_info` VALUES ('巴黎危机', '詹姆斯·瓦特金斯', 'James Thomas Watkins', '詹姆斯瓦特金斯', '詹姆斯·瓦特金斯 James Watkins  出生于1978年 是一名英国编剧和导演 在知名制片公司经过10年磨练 年轻的詹姆斯·瓦特金斯 极具写作才华与说故事天赋 2002年与Marc Evans导演合写了 谁在网路偷窥 的剧本 该片从当代最常见的网路安全问题出发 完美结合心理惊悚 偷窥欲 暴力 已可列为A级虐杀片的层次 07年的作品则是为Ringan Ledwige导演所撰写的 Gone  猎人游戏 又名 伊甸湖  是詹姆斯·瓦特金斯的第一部自编自导之作 首部作品即入围2008西班牙奇幻影展 并受各方媒体好评 可见其潜力无穷 本片更在2008金马国际观摩影展午夜狂热单元放映 广受好评 并让他获得了2008年英国独立电影奖Douglas Hickox Award的提名 詹姆斯最知名作品是2012年的惊悚片 黑衣女人  ', '2002年\n 谁在网络偷窥 \nMy Little Eye\n编剧　　\n2007年\n 踪迹难寻 \nGone\n编剧\n2008年\n 伊甸湖 \nEden Lake\n编剧 导演\n2008年英国独立电影奖提名\n2009年\n 黑暗侵袭2 \nThe Descent Part 2\n编剧\n2012年\n 黑衣女人 \nThe Woman in Black\n编剧 导演2014年  成王败寇 绝不2   暂译  Second Is Nowhere 编剧 导演', '伊甸湖 黑衣女人 黑暗侵袭2', '2008年英国独立电影奖提名', '0000-0-0', '英国诺丁山', '', '英国', '日耳曼', '', 0, 0, '金牛座', 'Natasha Galloway', '导演 编剧', '', '', '', 0, 0);
