INSERT INTO `director_info` VALUES ('玛丽和马克思', '亚当·艾略特', 'Adam Elliot', '', '亚当·艾略特，男，1972年1月2日出生于澳大利亚。他的自述三部曲《叔叔》、《表妹》和《兄弟》在300多个电影节上获得了超过50多项大奖，而《裸体哈维闯人生》则获得了第76届奥斯卡最佳动画短片奖。', '玛丽和马克思（2009）\n第76届奥斯卡颁奖典礼(2004)\n裸体哈维闯人生(2003)\n兄弟(1999)\n表妹(1998)\n叔叔(1996)', '玛丽和马克思', '2004年凭借动画短片《裸体哈维闯人生》捧得了人生中的第一座小金人。', '0000-0-0', '澳大利亚 ', '澳大利亚', '澳大利亚', '', '', 0, 0, '摩羯座', '', '导演              编剧              剪辑              制片              ', '', '', '', 0, 0);
INSERT INTO `director_info` VALUES ('无敌破坏王', '里奇·摩尔 ', 'Rich Moore', '', '里奇·摩尔(RichMoore，1963年5月10日-)是一名美国动画导演，也是动画公司RoughDraftStudios的商业合伙人，该公司最知名的作品是《辛普森一家》。', '疯狂动物城 2016　 \n无敌破坏王 2012 \n飞出个未来 1999 \n辛普森一家', '辛普森一家        疯狂动物城', '第40届安妮奖最佳动画电影、最佳导演、最佳编剧、最佳配音和最佳配乐五个大奖', '0000-0-0', '美国\n', '美国', '美国', '', '', 0, 0, '', '', '导演                演员              编剧                 副导演', '', '', '', 0, 0);
INSERT INTO `director_info` VALUES ('穿越时空的少女', '细田守', 'Mamoru Hosoda', '', '细田守，1967年9月19日出生于日本富山县中新川郡上市町，毕业于金泽美术工艺大学工艺学部美术科油画专业，日本动画导演，原动画师。', '2015年 怪物之子\n2012年 狼之子雨与雪\n2009年 夏日大作战\n2006年 穿越时空的少女\n2005年 海贼王电影版2005：狂欢男爵与神秘岛\n2004年 混沌武士\n2000年 数字精灵\n1999年 海贼王', '穿越时空的少女怪物之子', '日本电影学院奖\n2013    第36届    最佳动画电影    狼之子雨与雪    （获奖）    \n 2010    第33届    最佳动画电影    夏日大作战    （获奖）    \n 2007    第30届    最佳动画电影    穿越时空的少女    （获奖）    \n安妮奖\n 2011    第38届    安妮奖-最佳动画电影导演[13]     夏日大作战    （提名）    ', '0000-0-0', '日本富山县中新川郡上市町     ', '日本', '日本', '', '', 0, 0, '', '', '动画导演', '金泽美术工艺大学', '', '', 0, 0);
