INSERT INTO `screenwriter_info` VALUES (27, '微微一笑很倾城', '顾漫', '', '乌龟 乌龟漫 漫漫', '顾漫 1981年10月21日出生于江苏宜兴    又名 泥泥妈 乌龟漫 戚采  拥有自己的专属论坛和官方网站 其写作风格大多较为温馨轻快 充满青春气息 以笔名 泥泥妈 专写耽美 因为写作很慢而被称为 乌龟漫  \n晋江原创网的驻站作家 曾经是 仙度瑞拉 杂志编辑 \n2005年以 何以笙箫默 一举成名 2010年凭借 微微一笑很倾城 获得第三届中国网络文学节最佳作者奖 2013年中国作家富豪榜第44名 至2014年 已出版过 何以笙箫默  微微一笑很倾城  杉杉来吃  骄阳似我 上  四本畅销言情小说 其中由', '2006年1月22日  何以笙箫默  朝华出版社\n2009年8月1日  微微一笑很倾城  江苏文艺出版社\n2011年9月1日  杉杉来吃  江苏文艺出版社\n2013年09月  骄阳似我 上册 花山文艺出版社\n 小白与精英 \n　　 原创 言情 近代现代 爱情 轻松\n　　 陆双宜 周宁叙 2006 10 15 09 09 27 连载中\n 有女好采花  原创 言情 古色古香 爱情 轻松 戚采 宁昔 小侯爷 2007 11 24 22 44 40 连载中\n 非我倾城  原创 言情 近代现代 爱情 正剧 肖湘 20', ' 何以笙箫默   微微一笑很倾城   杉杉来吃   骄阳似我 上  ', '2013年中国作家富豪榜第44名 \n20162015 当当年度影响力作家 青春成长作家榜  2010年获得第三届中国网络文学节最佳作者奖 ', '1981-10-21', '江苏宜兴', NULL, NULL, '', '中国', '汉族', '', '作家 编辑', '南京审计大学', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (28, '三傻大闹宝莱坞', '拉库马·希拉尼', 'Rajkumar Hirani', '', '拉库马·希拉尼  Rajkumar Hirani 男 1962年11月22日出生印度电影导演、编剧和剪接师 他获得过印度国家电影奖和印度电影观众奖 主要作品有2009年的喜剧片 三个傻瓜 2014年的喜剧片 外星醉汉PK地球神 等', '参演 黑帮大佬医生梦 三傻大闹宝莱坞 导演三傻大闹宝莱坞 外星醉汉PK地球神 我的个神啊 黑道大哥再出击', '三傻大闹宝莱坞 黑道大哥再出击 黑帮大佬医生梦 外星醉汉PK地球神', '印度国家电影奖 印度电影观众奖', '1962-11-22', '', NULL, NULL, '', '印度', '', '', '', '', '', '', NULL);
