INSERT INTO `screenwriter_info` VALUES (30, '这个杀手不太冷', '吕克·贝松', 'Luc Besson', '', '吕克·贝松 Luc Besson  1959年3月18日生于法国巴黎 法国导演 兼任制片、编剧、演员和剪辑等  1983年 拍摄剧情长片处女作 最后决战 获戛纳电影节特别奖 1985年 执导他的第二部影片 地铁  获第11届法国电影恺撒奖十三项提名和三项奖 1987年的 碧海蓝天 获第14届恺撒奖八项提名和两项奖 1989年的 尼基塔 获第16届恺撒奖九项提名和一项奖 1997年 拍摄想象力四溢的科幻片 第五元素  1994年 执导拍摄 这个杀手不太冷  也因这部作品被中国观众熟识  1999年吕克·贝松', '导演作品 最后决战 地铁 碧海蓝天 尼基塔 亚特兰蒂斯 第五元素 编剧 的士速递2 编剧 绿芥刑警 编剧 制片 龙之吻 编剧 玩命速递 倒数第二 最后决战 地下铁 碧海蓝天 尼基塔 亚特兰蒂斯 这个杀手不太冷 第五元素 圣女贞德 天使A  亚瑟和他的迷你王国 亚瑟和他的迷你王国2 阿黛拉的非凡冒险 亚瑟3终极对决 昂山素季 别惹我 超体 瓦尔瑞恩与千星之城 编剧作品 亚瑟和他的迷你王国 侠盗魅影 出租车4  飓风营救 玩命快递3 暴力街区13终极  亚瑟和他的迷你王国2 阿黛拉的非凡冒险 巴黎谍影 尼基塔', '这个杀手不太冷 圣女贞德 超体', '英国电影学院奖  1998-01    第51届英国电影学院奖亚历山大•柯达奖最佳英国电影    切勿吞食     获奖       1986-01    第39届英国电影学院奖最佳外语片奖 提名      上海国际电影节  2010-06    第13届上海国际电影节杰出电影贡献奖 获奖      日本电影学院奖  1996    第19届日本电影学院奖最佳外语片奖 这个杀手不太冷 提名       1992    第15届日本电影学院奖最佳外语片奖 提名      法国电影凯撒奖  2012    第', '1959-3-18', '巴黎', NULL, NULL, '', '法国', '', '', '导演 编剧 制片 演员 剪辑', '', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (31, '少年派的奇幻漂流', '大卫·马戈', 'David Magee', '戴维·马吉', '大卫·马戈\nDavid Magee\n职业 编剧\n出生日期 1962\n又译 戴维·马吉\n出生地 美国密歇根州 大卫·马戈 1962年出生 美国电影编辑 代表作是 少年派的奇幻漂流 ', '2012\n 少年派的奇幻漂流  编剧\n2008\n 明星助理  编剧\n2004\n 寻找梦幻岛  编剧', '少年派的奇幻漂流', '', NULL, '', NULL, NULL, '', '美国', '', '', '编剧', '', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (32, '', '扬·马特尔', 'Yann Martel', '', '扬·马特尔 Yann Martel 1963年6月25日  是一位加拿大作家 他出生于西班牙萨拉曼卡 父母是加拿大人 \n幼时曾旅居哥斯达黎加 法国 墨西哥 加拿大 成年后做客伊朗 土耳其及印度 毕业于加拿大特伦特大学哲学系 其后从事过各种稀奇古怪的行业 包括植树工 洗碗工 保安等   以 少年Pi的奇幻漂流  获得2002年的布克奖及亚洲 太平洋美洲文学奖 马特尔现在住在萨斯卡通 Saskatoon ', '1993年  七个故事 \n1993年  赫尔辛基罗氏家族的幕后真相 故事的真相  The Facts Behind the Helsinki Roccamatios 张定绮译 皇冠出版社 \n1996年  自我 \n2001年  少年Pi的奇幻漂流  赵丕慧译 皇冠出版社 \n 插图制作 托米斯拉夫·托亚纳克 \n2004年  We Ate the Children Last \n2010年  标本师的魔幻剧本  柯乃瑜译 商周出版 ', '少年Pi的奇幻漂流', '2002年的布克奖及亚洲 太平洋美洲文学奖', '1963-6-25', '', NULL, NULL, '', '加拿大', '', '', '', '', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (33, '我的战争', '刘恒', '', '刘冠军', '刘恒 1954.5    男 本名刘冠军 北京斋堂人 汉族 大专学历 无党派人士 有 中国第一编剧 之称 \n曾从事过工农兵三业 70年代末开始文学创作 因发表风格独特的 狗日的粮食 获第八届全国优秀短篇小说奖而引起文坛关注 此后 就发表了 白涡   虚证   伏羲伏羲   教育诗   黑的雪 等中长篇小说 已有五卷本 刘恒自选集 问世 中篇小说 伏羲伏羲 改编的电影 菊豆 成为第一部获得奥斯卡最佳外语片提名的中国电影  黑的雪 被改编成电影 本命年 获柏林电影节银熊奖 中篇小说 贫嘴张大民的幸福生活 改编', '文学作品\n长篇小说 黑的雪  逍遥颂  苍河白日梦 \n中篇小说 贫嘴张大民的幸福生活  天知地知 \n短篇小说 狗日的粮食  拳圣 等\n电影作品\n1990年 本命年 \n1990年 菊豆 \n1992年 四十不惑 \n1992年 秋菊打官司 \n1994年 西楚霸王 \n1994年 红玫瑰白玫瑰 \n1995年 跟我走一回 \n1995年 画魂 \n1998年 没事偷着乐 \n1999年 漂亮妈妈 \n2004年 张思德 \n2006年 云水谣 \n2007年 集结号 \n2009年 铁人 \n2011年 乱世胭脂 \n2011年 金陵', '狗日的粮食 苍河白日梦 贫嘴张大民的幸福生活', '第25届中国电影金鸡奖最佳编剧奖 \n第45届台湾电影金马奖最佳编剧奖 \n第12届中国电影华表奖优秀编剧奖 \n第一届鲁迅文学奖 \n第一届老舍文学奖', '1954-5-11', '北京', NULL, NULL, '', '中国', '', '', '编剧 作家', '', '', '', NULL);
