INSERT INTO `screenwriter_info` VALUES (39, '寒战2', '陆剑青', 'Kim-ching Luk ', '', '陆剑青 Kim-ching Luk   1967年2月11日出生于中国香港 香港电影导演、编剧 \n多年来担任众多港片的副导演和助理导演 其中包括不少B级片 偶尔也在银幕里客串小角色 常与梁乐民一起出现在同一部影片的幕后 有2008年王晶监制 陈小春主演的 黑势力  陆剑青任副导演 梁乐民任美术 \n2012年11月8日 首次与梁乐民共同指导的电影 寒战  与梁乐民同时获得第32届香港电影金像奖最佳导演奖和最佳编剧奖 \n2015年4月30日 与梁乐民共同执导的电影 赤道 全国公映  \n2016年7月8日 与梁', '执导作品 2016年　 寒战2 \n2015年　 赤道 \n2012年 寒战  编剧作品2016年　 寒战2 \n2015年 赤道 \n2012年 寒战', '寒战 寒战2 赤道', '第32届香港电影金像奖最佳导演奖  \n第32届香港电影金像奖最佳编剧奖 \n', '1967-2-11', '中国香港', NULL, NULL, '', '中国', '', '', '导演 编辑 副导演', '', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (NULL, '', '梁乐民', 'Lok Man Leung', '', '梁乐民 Lok Man Leung  1972年3月23日出生于中国香港 香港电影美术指导、导演和编剧 \n2012年 首次与陆剑青共同自编自导犯罪动作电影 寒战 在11月8日上映 获得第32届香港电影金像奖最佳编剧奖和最佳导演奖\n2015年4月30日 与陆剑青共同执导的电影 赤道 全国公映\n2016年7月8日 与陆剑青共同执导的电影 寒战2 全国公映  该片成为第19届上海国际电影节的开幕影片 ', '执导作品 2016年　 寒战2 \n2015年　 赤道 \n2012年 寒战  编剧作品2016年　 寒战2 \n2015年 赤道 \n2012年 寒战', '寒战 寒战2 赤道', '第32届香港电影金像奖最佳导演奖 \n第32届香港电影金像奖最佳编剧奖\n', '1972-3-23', '中国香港', NULL, NULL, '', '中国', '', '', '导演 编剧 美术指导', '', '', '', NULL);
