INSERT INTO `screenwriter_info` VALUES (52, '从你的全世界路过', '张嘉佳', '', '', '张嘉佳 1980年6月22日出生于江苏南通 作家 编剧 导演 毕业于南京大学 \n张嘉佳大学毕业后担任过杂志主笔 电视编导等 [1]  2005年 出版首部长篇小说 几乎成了英雄  2010年 出版小说 情人书   2011年首次担任电影编剧 凭借 刀见笑 获得第48届台湾电影金马奖最佳改编剧本提名   2013年 出版书籍 从你的全世界路过  一年销售超过400万册 创下单本小说历史记录  并入选第五届中国图书势力榜文学类十大好书   2014年7月 8月 担任江苏卫视 非诚勿扰 的现场点评嘉宾   20', '2005年  几乎成了英雄  小说\n2007年  小夫妻天天恶战  小说\n2010年  情人书  小说\n2011年  刀见笑  小说\n2013年  从你的全世界路过  小说\n2014年  让我留在你身边  小说\n2011年　  刀见笑  电影\n2015年  摆渡人  电影\n2015年  从你的全世界路过  电影\n', '情人书 几乎成了英雄 从你的全世界路过 让我留在你身边', '2011年台湾电影金马奖最佳改编剧本提名 \n2014中国年度好书文学艺术类好书之一 \n2014年中国作家榜第一名 \n2014年中国作家榜年度畅销书 \n 2014年中国书业年度作家', '1980-6-22', '江苏南通', NULL, NULL, '', '中国', '', '', '作家 编剧 导演', '南京大学', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (53, '魔法老师', '特瑞·琼斯', 'Terry Jones', '', '特瑞·琼斯 1942年2月1日出生于威尔士 导演 喜剧家 剧作家 演员 主要作品有 撒谎精自传  等', '撒谎精自传  \n 不是救世主  他是一个非常顽皮的孩子  \n 吉尔劳姆一世  \n 第七成员 \n1969-1974 蒙提·派森的飞行马戏团  电视剧', '撒谎精自传', '', '1942-2-1', '威尔士', NULL, NULL, '', '', '', '', '导演 喜剧家 剧作家 演员', '', '', '', NULL);
INSERT INTO `screenwriter_info` VALUES (54, '长江图', '杨超', '', '', '第六代新锐导演代表人物之一 河南信阳人 毕业于北京电影学院 中国电影导演协会会员 国家二级导演 中国戏曲学院导演系影视导演专业教研室主任 早年参与 实践社  并策划组织相关艺术电影推广活动 同时也兼有影视批评写作等 2001年凭借 待避 获得戛纳国际电影节电影基石奖及台北电影节国际学生电影银狮奖 2004年凭借 旅程 获得第57届戛纳国际电影节最佳处女作特别奖 金摄影机特别奖  是国内鲜有戛纳持续入围导演 亦被评论界称之为 戛纳嫡系 导演 最新十年磨一剑的奇幻爱情巨制 长江图  因其磅礴壮阔的史诗气质 在', '1996\n灯\n故事短片\n10分钟\n彩色\n1997\n待避\n故事短片\n29分钟\n彩色\n1998\n人生百味\n电视连续剧\n5集\n彩色\n1998\n爱我家园\n电视连续剧\n9集\n彩色\n1999\n晋颂\n历史文化纪录片\n18集\n彩色\n2002\n旅程\n剧情片\n112分钟\n彩色\n2005\n信使六章\n纪录片\n30分钟\n彩色\n2005\n窗前随笔\n纪录片\n5分钟\n黑白\n2006\n唐碑记\n剧情片\n90分钟\n彩色\n2008\n雨来\n剧情片\n89分钟\n彩色\n2016\n长江图\n剧情片\n120分钟\n彩色', '旅程   长江图', '戛纳电影节电影基石奖 \n台北电影节国际学生电影银狮奖 \n戛纳电影节最佳处女作特别奖 \n光州电影节评委会奖 \n台湾金马影展中影后制大奖', '1974-1-6', '河南信阳', NULL, NULL, '', '中国', '', '', '导演 编剧 教师', '北京电影学院', '', '', NULL);
